    protected function getTextsAndSeo($item): array
    {
        $texts = [];
        $seo = [];

        foreach ($this->keysTexts as $keyText) {
            if (isset($item[$keyText])) {
                $texts[$keyText] = $item[$keyText];
                unset($item[$keyText]);
            } else {
                $texts[$keyText] = null;
            }

            foreach ($this->keysSeo as $keySeo) {
                $fullKeySeo = $keySeo.'_'.$keyText;
                if (isset($item[$fullKeySeo])) {
                    $seo[$fullKeySeo] = $item[$fullKeySeo];
                    unset($item[$fullKeySeo]);
                } else {
                    $seo[$fullKeySeo] = null;
                }
            }
        }
        return [$item, $texts, $seo];
    }

    protected function getSeo($item): array
    {
        $seo = [];
        foreach ($this->keysSeo as $keySeo) {
            if (isset($item[$keySeo])) {
                $seo[$keySeo] = $item[$keySeo];
                unset($item[$keySeo]);
            } else {
                $seo[$keySeo] = null;
            }
        }
        return [$item, $seo];
    }

    protected function setTextsAndSeo(int $id, string $foreignKeyName, array $texts, array $seo, array $models): void
    {
        foreach ($this->keysTexts as $keyText) {
            if ($texts[$keyText]) {
                if (is_array($texts[$keyText])) {
                    foreach ($texts[$keyText] as $k => $item) {
                        $model = $models[$keyText]->create([
                            'text' => $item,
                            $foreignKeyName.'_id' => $id
                        ]);

                        if (!$k) $this->setSeo($model->id, $foreignKeyName.'_'.$keyText.'_id', $keyText, $seo);
                    }
                } else {
                    $model = $models[$keyText]->create([
                        'text' => $texts[$keyText],
                        $foreignKeyName.'_id' => $id
                    ]);
                    $this->setSeo($model->id, $foreignKeyName.'_'.$keyText.'_id', $keyText, $seo);
                }
            }
        }
    }

    protected function setSeo(int $id, string $foreignKeyName, string $keyText, array $seo): void
    {
        $seoItem = [$foreignKeyName => $id];
        $existsSeoFlag = false;
        foreach ($this->keysSeo as $keySeo) {
            $fullKeySeo = $keyText ? $keySeo . '_' . $keyText : $keySeo;
            if ($seo[$fullKeySeo]) {
                $existsSeoFlag = true;
                $seoItem[$keySeo] = $seo[$fullKeySeo];
            }
        }
        if ($existsSeoFlag) Seo::create($seoItem);
    }
